import React, { useEffect, useRef, useState, createContext } from 'react';
import { ModalCreating } from './components/Modal';
import { TreesMain } from './components/treesMain';
import { MainTree } from './types/Tree';


export const App: React.FC = () => {
  const [mainTrees, setMainTrees] = useState<MainTree[]>([]);
  const [isError, setIsError] = useState(false);
  const [errorMessage, setErrorMessage] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [isModalOpen, setIsModalOpen] = useState(false);

  return ( 
    <div className="treesapp">
      <h1 className="treesapp__title"> Trees </h1>

      <button 
        className="button is-warning"
        onClick={() => setIsModalOpen(true)}
      > 
        Create new tree
      </button>

      {isModalOpen
      ? < ModalCreating 
          mainTrees={mainTrees} 
          onSetMainTrees={setMainTrees}
          onSetIsModalOpen={setIsModalOpen}
        />
      : <div className="treesapp__content">
          {mainTrees
            ? < TreesMain mainTrees={mainTrees}/>
            : <h2 className="treesapp__description"> No trees </h2>
          }
        </div>
      }
    </div>
  );
};
